SET NAMES UTF8;
DROP DATABASE IF EXISTS newtust;
CREATE DATABASE newtust CHARSET = UTF8;
USE newtust;

#用户表
CREATE TABLE user(
    uid INT PRIMARY KEY,
    uname VARCHAR(16),
    upwd VARCHAR(32),
    udepartment VARCHAR(32),
    upro VARCHAR(32)
);
#SELECT * FROM user;

#主页大图表
CREATE TABLE idx_img(
    pid INT PRIMARY KEY AUTO_INCREMENT,
    psrc VARCHAR(64),
    phref VARCHAR(64),
    ptitle VARCHAR(32),
    ptime BIGINT
);
#SELECT * FROM idx_img;

#新闻表
CREATE TABLE news(
    nid INT PRIMARY KEY AUTO_INCREMENT,
    nauthor VARCHAR(16),
    ntitle VARCHAR(64),
    nabstract VARCHAR(1024),
    ncontent VARCHAR(10240),
    ndate BIGINT
);
#SELECT * FROM news;

#公告表
CREATE TABLE notice(
    nid INT PRIMARY KEY AUTO_INCREMENT,
    nauthor VARCHAR(16),
    ntitle VARCHAR(64),
    nabstract VARCHAR(1024),
    ncontent VARCHAR(10240),
    ndepartment VARCHAR(64),
    ndate BIGINT
);
#SELECT * FROM notice;

#创建专题表
CREATE TABLE special(
    nid INT PRIMARY KEY AUTO_INCREMENT,
    nauthor VARCHAR(16),
    ntitle VARCHAR(64),
    nabstract VARCHAR(1024),
    ncontent VARCHAR(10240),
    ndate BIGINT
);
#SELECT * FROM special;

